namespace WebServicesAnticiposNomina.Core
{
    public class ApiCobreCore
    {
        private readonly IConfiguration _configuration;

        public ApiCobreCore(IConfiguration configuration)
        {
            _configuration = configuration;
        }

        public ResponseCobre PostPaymentAdvance(DataTable dataUser, string Token)
        {
            ResponseCobre responseModels = new();
            try
            {
                responseModels.Message = "Token expirado";
                responseModels.code = "401";

                SecurityCore securityCore = new(_configuration);
                ApiCobre apiCobre = new(_configuration);

                Utilities utilities = new(_configuration);
                string TokenApi = apiCobre.PostAuthToken(Token);
                if (TokenApi != "false")
                {
                    int Balance = apiCobre.GetBalanceBank(TokenApi);

                    if (Balance > 0)
                    {
                        var paymant = PutPaymentClass(dataUser);
                        if (paymant != null)
                        {
                            responseModels = apiCobre.PostPayment(TokenApi, paymant);
                        }
                        else
                        {
                            responseModels.Message = "Faltan datos personales";
                            responseModels.code = "204";
                        }
                        string html = "<p class=\"MsoNormal\" style=\"text-align: center;\"><strong style=\"mso-bidi-font-weight: normal;\"><span lang=\"ES\" style=\"font-family: sans-serif; mso-ansi-language: ES;\">CONTRATO DE TRABAJO A T&Eacute;RMINO FIJO </span></strong><strong><span lang=\"ES\" style=\"font-family: sans-serif; mso-ansi-language: ES;\">ESTRATEGIA DIGITAL Y COMERCIAL S.A.S.</span></strong></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\"><span style=\"font-family:  sans-serif;\">Entre <strong>ESTRATEGIA DIGITAL Y COMERCIAL S.A.S.</strong>, sociedad comercial, del tipo de las sociedades por acciones simplificadas, colombiana, con domicilio en la ciudad de Medell&iacute;n, identificada con NIT. 901.658.649-2, de una parte, quien en adelante se denominar&aacute; <strong>EL EMPLEADOR</strong>; y (NOMBRE PERSONA)</span></span><span style=\"font-family:  sans-serif;\">&nbsp;<span lang=\"ES\">, colombian<a>o</a></span><span lang=\"ES\">, mayor de edad, vecino de la ciudad de <a>Medell&iacute;n</a></span><span lang=\"ES\">, identificado con la c&eacute;dula de ciudadan&iacute;a No. (CEDULA)</span>&nbsp;<span lang=\"ES\">, de otra parte, quien en adelante se denominar&aacute; <strong>EL EMPLEADO</strong>, se ha celebrado un <strong>CONTRATO INDIVIDUAL DE TRABAJO A T&Eacute;RMINO FIJO</strong>, el cual se formaliza por el presente documento, por el Reglamento Interno de Trabajo, y por los reglamentos especiales de cada uno de los cargos que se desarrollan o llegaren a desarrollar por <strong>EL EMPLEADOR</strong>.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"> </span></p>\r\n<div style=\"text-align: justify;\">\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong>CUERPO CLAUSULAR</strong></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong>PRIMERO. Objeto del Contrato. </strong>El presente contrato tiene por objeto la contrataci&oacute;n directa por parte de <strong>EL EMPLEADOR</strong> de los servicios profesionales y personales de <strong>EL EMPLEADO </strong>para el cargo de <strong>ASESOR DE CALL CENTER, </strong>oblig&aacute;ndose este &uacute;ltimo a:</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>a.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>Poner al servicio de <strong>EL EMPLEADOR</strong> toda su capacidad normal de trabajo, en forma exclusiva, en el desempe&ntilde;o de las funciones propias de su oficio, as&iacute; como en las labores anexas y complementarias del mismo, de conformidad con las normas laborales vigentes, las cuales declara conocer y acatar.</span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>b.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>Cumplir las &oacute;rdenes e instrucciones que le imparta <strong>EL EMPLEADOR</strong> o sus representantes, todo lo cual forma parte integrante del presente contrato para todos los efectos legales. </span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>c.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>A guardar estricta reserva de todo lo que llegue a su conocimiento por raz&oacute;n de su oficio y cuya comunicaci&oacute;n con otros pudiera causar perjuicios a <strong>EL EMPLEADOR</strong> y/o a sus clientes.</span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>d.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>A trabajar la jornada ordinaria, seg&uacute;n el horario de trabajo implementado por <strong>EL EMPLEADOR</strong>.</span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>e.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>A prestar sus servicios en igualdad de condiciones laborales en cualquiera de los establecimientos de la Instituci&oacute;n Universitaria ITM y/o desde su casa, seg&uacute;n lo se&ntilde;alado por <strong>EL EMPLEADOR</strong>.</span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>f.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>A dar aviso oportuno a <strong>EL EMPLEADOR</strong>, cuando por cualquier causa no pueda concurrir al trabajo.</span></p>\r\n<span style=\"font-family:  sans-serif;\"> </span>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18.0pt; text-indent: -18.0pt; mso-list: l0 level1 lfo1;\"><span style=\"font-family:  sans-serif;\"><strong>g.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>A respetar todas las leyes vigentes sobre la propiedad intelectual, seguridad y las pol&iacute;ticas internas de <strong>EL EMPLEADOR</strong> en todo lo relacionado con la administraci&oacute;n y uso de informaci&oacute;n y documentaci&oacute;n relacionada con los elementos que tiene a su disposici&oacute;n o llegare a tener. <strong>EL EMPLEADO</strong> autoriza a <strong>EL EMPLEADOR</strong> para que acceda a la cuenta de correo electr&oacute;nico, al disco de la estaci&oacute;n de trabajo y/o al tel&eacute;fono celular o m&oacute;vil, que est&eacute;n bajo su administraci&oacute;n o llegaren a estarlo, con el prop&oacute;sito de que constate el contenido de los mismos cuando as&iacute; lo considere.</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong style=\"text-indent: -18pt;\">h.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span style=\"text-indent: -18pt;\">A cumplir a cabalidad con todas y cada una de las funciones establecidas por </span><strong style=\"text-indent: -18pt;\">EL EMPLEADOR</strong><span style=\"text-indent: -18pt;\"> para el buen desempe&ntilde;o del cargo para el cual fue contratado.</span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong>SEGUNDA. Obligaciones Especiales de EL EMPLEADO. </strong>En desarrollo del presente contrato, y para el debido cumplimiento del mismo, <strong>EL EMPLEADO</strong> est&aacute; obligado a cumplir con las asignadas por <strong>EL EMPLEADOR.</strong></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong>TERCERO. Remuneraci&oacute;n. </strong>Por la prestaci&oacute;n del servicio a que se obliga <strong>EL EMPLEADO</strong> conforme las cl&aacute;usulas del contrato, <strong>EL EMPLEADOR</strong> le pagar&aacute; un salario en dinero de (SALARIO)<strong>&nbsp;</strong>mensuales. El salario ser&aacute; pagadero en dos (2) quincenas vencidas, mediante el sistema de pago establecido por <strong>EL EMPLEADOR</strong>, los d&iacute;as 15 y 30 de cada mes.</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family:  sans-serif;\"><strong>PAR&Aacute;GRAFO. </strong>Ser&aacute; de cargo de <strong>EL EMPLEADOR</strong> el pago de los aportes a la seguridad social en salud y pensiones, ARL, prestaciones sociales, parafiscales y dem&aacute;s obligaciones laborales a que haya lugar seg&uacute;n la normativa vigente aplicable al contrato de trabajo que suscriben las partes, los cuales se pagar&aacute;n en los t&eacute;rminos establecidos en la Ley Laboral.</span></p>\r\n<span style=\"font-family:  sans-serif;\"><strong><span style=\"font-size: 11pt; line-height: 107%;\">CUARTO. Duraci&oacute;n del Contrato. </span></strong><span style=\"font-size: 11pt; line-height: 107%;\">El presente contrato tendr&aacute; como fecha de inicio el d&iacute;a&nbsp; (FECHA INICIO), y su duraci&oacute;n ser&aacute; hasta el d&iacute;a (FECHA TERMINACION)<strong>. </strong>Conforme lo permiten los art&iacute;culos 76 a 80 del C&oacute;digo Sustantivo del Trabajo, el presente contrato tendr&aacute; un periodo de prueba equivalente a la quinta parte del t&eacute;rmino de duraci&oacute;n inicialmente pactado para el contrato. En consecuencia, durante el per&iacute;odo de prueba, <strong>EL EMPLEADOR </strong>puede dar por terminado el contrato unilateralmente en cualquier momento, sin previo aviso, y sin que haya lugar a indemnizaci&oacute;n de ning&uacute;n tipo a favor de <strong>EL EMPLEADO</strong></span></span></div>\r\n<div style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"> </span>\r\n<div style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"> </span></div>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>QUINTO. Jornada Laboral. EL EMPLEADO</strong> se obliga a laborar la jornada ordinaria de CUARENTA Y SIETE (47) HORAS semanales en los t&eacute;rminos y dentro de las horas se&ntilde;aladas por <strong>EL EMPLEADOR</strong>, pudiendo este hacer ajustes o cambios de horarios cuando lo estime conveniente. Por el acuerdo expreso o t&aacute;cito de las partes, podr&aacute;n repartirse las horas de la jornada ordinaria en la forma prevista en el Art&iacute;culo 23 de la Ley 50 de 1990, teniendo en cuenta que los tiempos de descansos entre las sesiones de las jornadas no se computan dentro de la misma seg&uacute;n lo establecido por el art&iacute;culo 167 del C&oacute;digo Sustantivo del Trabajo.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\">Todo trabajo suplementario o en horas extras, o toda modificaci&oacute;n de jornada hasta la m&aacute;xima legal y todo trabajo en d&iacute;a domingo o festivo en los que legalmente debe concederse descanso, mientras no sea labor que seg&uacute;n la ley o el contrato deba ejecutarse as&iacute;, debe estar autorizado por <strong>EL EMPLEADOR</strong> o sus representantes previamente por escrito. Cuando la necesidad de ese trabajo se presente de manera imprevista o inaplazable, deber&aacute; ejecutarse y darse cuenta de &eacute;l por escrito con la mayor brevedad a <strong>EL EMPLEADOR</strong> o a sus representantes. <strong>EL EMPLEADOR,</strong> por consiguiente, no reconocer&aacute; ning&uacute;n trabajo suplementario o en d&iacute;as de descanso legalmente obligatorio que no haya sido autorizado previamente o avisado en forma inmediata.&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\">La jornada de <strong>EL EMPLEADO</strong> estar&aacute; comprendida de la siguiente manera, sin perjuicio de que ella pueda ser modificada en cualquier momento por <strong>EL EMPLEADOR</strong> bajo los lineamientos establecidos en el presente contrato: de lunes a jueves ser&aacute; de 8:00 a.m. a 13:00.pm. y de 14:00 p.m. a 6:00 p.m. Los d&iacute;as viernes ser&aacute; de 8:00 a.m. a 13:00 pm y de 14:00.m. a 16:30 p.m.</span>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\">Por lo anterior, la jornada diaria comprendida entre los d&iacute;as lunes a jueves ser&aacute; de 9 horas y el d&iacute;a viernes de 7,5 horas, sin que se constituya trabajo extra.&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>SEXTO. Traslados y Modificaciones. EL EMPLEADO</strong> acepta desde ahora los traslados de lugar de trabajo y cambios de oficio que determine <strong>EL EMPLEADOR</strong>, siempre que ellos no entra&ntilde;en desmejora de la remuneraci&oacute;n, ni de la categor&iacute;a de <strong>EL EMPLEADO.</strong> As&iacute; mismo, las partes acuerdan que <strong>EL EMPLEADO</strong> podr&aacute; laborar conforme lo permite la jornada flexible contenida en el art&iacute;culo 51 de la Ley 789 de 2002.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">&nbsp;<span style=\"font-family:  sans-serif;\"><strong>S&Eacute;PTIMO. Terminaci&oacute;n del Contrato. </strong>Son justas causas para poner t&eacute;rmino a este contrato, unilateralmente por parte de <strong>EL EMPLEADOR</strong>, las enumeradas en el literal A) del art&iacute;culo 62 del C&oacute;digo Sustantivo del Trabajo, as&iacute; como el incumplimiento de <strong>EL EMPLEADO</strong> de lo establecido en los art&iacute;culos 58 y 60 del C&oacute;digo Sustantivo del Trabajo. </span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">&nbsp;<span style=\"font-family:  sans-serif;\">As&iacute; mismo es justa causa para dar por terminado el contrato de trabajo la violaci&oacute;n de cualquiera de las obligaciones, prohibiciones y deberes que incumben a <strong>EL EMPLEADO </strong>contemplados en el presente contrato, en el Reglamento Interno de Trabajo vigente (si lo hubiere), en los manuales de funciones, perfil y descripci&oacute;n de cargo (si los hubiere), memorandos, etc., cuya violaci&oacute;n se considera tambi&eacute;n falta grave.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">De conformidad con lo previsto por el numeral 6 del literal A del art&iacute;culo 62 del C&oacute;digo Sustantivo del Trabajo, adem&aacute;s de las que consagra la Ley Laboral, son justas causas para dar por terminado el presente contrato de trabajo unilateralmente por parte de <strong>EL EMPLEADOR</strong>, sin previo aviso y sin pago de indemnizaci&oacute;n alguna, las siguientes faltas, las cuales las partes acuerdan calificar como especialmente graves:</span><span lang=\"ES\" style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>a.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong>El hecho de que <strong>EL</strong> <strong>EMPLEADO</strong> difunda informaci&oacute;n comercial, financiera, contable, propiedad intelectual, etc., del mismo <strong>EMPLEADOR</strong>, o de uno cualquiera de sus clientes, sin previa autorizaci&oacute;n por escrito de <strong>EL EMPLEADOR.</strong></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>b.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Desacreditar en alguna forma a <strong>EL EMPLEADOR, </strong>uno cualquiera de sus clientes y/o lo representantes legales y/o administradores, con manifestaciones o actos de cualquier naturaleza considerados como actos de descr&eacute;dito.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>c.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Que <strong>EL EMPLEADO</strong> reciba, solicite o exija por cualquier medio dinero, regalo, d&aacute;diva o favor, de parte de cualquier empleado de <strong>EL EMPLEADOR, </strong>o de cualquier cliente interno o externo, distinto de la remuneraci&oacute;n salarial o de las prestaciones a las que tiene derecho en virtud del contrato de trabajo suscrito con <strong>EL EMPLEADOR</strong>.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>d.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">S&iacute; <strong>EL EMPLEADO</strong> llegara a lucrarse de bienes o dinero que se entienden son de propiedad de <strong>EL EMPLEADOR</strong> y/o de uno cualquiera de sus clientes. Lo anterior sin perjuicio de las acciones legales que puedan adelantarse ante las autoridades respectivas.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>e.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Desatender o negarse a cumplir medidas de control establecidas por <strong>EL EMPLEADOR</strong>, para prevenir accidentes, hurtos, estafas o cualquier otro il&iacute;cito que puedan atentar contra el patrimonio de <strong>EL EMPLEADOR</strong> y/o de uno cualquiera de sus clientes.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>f.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">El hecho de que <strong>EL EMPLEADO</strong> llegue a laborar en estado de embriaguez o bajo la influencia de narc&oacute;ticos o drogas enervantes.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>g.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">El consumo de cigarrillo, alcohol o sustancias alucin&oacute;genas dentro de las instalaciones de <strong>EL EMPLEADOR </strong>y/o uno cualquiera de sus clientes<strong> </strong>o en su lugar de trabajo, ya sea en jornada laboral o fuera de ella.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>h.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Sustraer o intentar sustraer art&iacute;culos, equipos de trabajo, aplicaciones de sistemas, bases de datos, activos de propiedad intelectual, productos, materias primas, enseres o mercanc&iacute;as de <strong>EL EMPLEADOR</strong>, de sus clientes o de los compa&ntilde;eros de trabajo. </span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>i.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Tener comportamiento irrespetuoso o desavenencias frecuentes con sus compa&ntilde;eros de trabajo, utilizar tonos desafiantes y elevados que a juicio de <strong>EL EMPLEADOR</strong> impidan la buena marcha de las labores.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>j.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Cualquier falta u omisi&oacute;n grave en el manejo de elementos y/o herramientas de trabajo.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>k.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Hacer afirmaciones falsas y graves sobre <strong>EL EMPLEADOR</strong> y todo lo que lo rodea como, por ejemplo: ejecutivos, clientes, servicios, productos, entre otros.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>l.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Negarse a utilizar adecuadamente los implementos de seguridad Industrial adoptados por&nbsp; <strong>EL EMPLEADOR</strong> o negarse a cumplir las medidas de higiene o seguridad.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>m.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Negarse a cumplir, sin justa causa, indicaciones que en el desempe&ntilde;o de su oficio le imparta <strong>EL EMPLEADOR</strong> o sus representantes, siempre y cuando las mismas tengan una relaci&oacute;n directa con las labores encomendadas y no afecten la dignidad de <strong>EL EMPLEADO</strong>.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>n.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">La utilizaci&oacute;n para fines distintos a la labor para la cual fue contratado <strong>EL EMPLEADO</strong> o en general cualquier uso ilegal o inadecuado de la informaci&oacute;n y/o secreto empresarial que le haya sido confiado para el cumplimiento a cabalidad de sus funciones. En este orden de ideas y bajo el anterior entendimiento, <strong>EL EMPLEADO</strong> autoriza expresamente a <strong>EL EMPLEADOR</strong> para que acceda a la cuenta de correo electr&oacute;nico, computador y/o cualquier equipo tecnol&oacute;gico que est&aacute; bajo su administraci&oacute;n, y/o que le sea asignado, con el prop&oacute;sito de que constate el contenido de la misma cuando as&iacute; lo considere, igualmente para que verifique con los medios que estime necesarios el uso que <strong>EL EMPLEADO</strong> haga de la conexi&oacute;n a la red de Internet dispuesta por&nbsp; <strong>EL EMPLEADOR </strong>y/o sus clientes, incluida la verificaci&oacute;n de las visitas a los sitios de la red que desde dicha cuenta haga <strong>EL EMPLEADO</strong>.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>o.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">La falta total al trabajo durante el d&iacute;a o en la jornada correspondiente, sin excusa suficiente a juicio de <strong>EL EMPLEADOR</strong>, a&uacute;n por la primera vez y de conformidad al Reglamento Interno de Trabajo vigente (en caso de aplicar).</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>p.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">La no asistencia puntual al trabajo sin excusa suficiente a juicio de <strong>EL EMPLEADOR</strong> y de conformidad al Reglamento Interno de Trabajo (en caso de aplicar).</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"> </span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>q.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><span lang=\"ES\">Incumplir cualquiera de las obligaciones previstas en el presente contrato y el Reglamento Interno de Trabajo, manual de funciones, perfil y descripci&oacute;n de cargo, memorandos, etc. (en caso de aplicar).</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">PAR&Aacute;GRAFO. </span></strong><span lang=\"ES\">Las faltas anteriormente indicadas no son taxativas dentro de los motivos de despido con justa causa, pues tambi&eacute;n tendr&aacute;n aplicaci&oacute;n para tal fin las que se desprendan de las normas legales y reglamentarias,</span><span lang=\"es\"> as&iacute; como cualquier otra que <strong>EL EMPLEADOR</strong> considere como justa causa para dar por terminado el contrato de trabajo</span><span lang=\"ES\">.</span></span><span lang=\"ES\" style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>OCTAVO. </strong>De acuerdo con el art&iacute;culo 15 de la Ley 50 de 1990, las partes acuerdan expresamente que no constituyen salario ni se computaran como factor de salario para ning&uacute;n efecto, los pagos o reconocimientos que se le hagan al <strong>EMPLEADO</strong> por concepto de beneficios o auxilios habituales o convencionales, acordados convencional o contractualmente u otorgados unilateralmente en forma extralegal por <strong>EL EMPLEADOR</strong>. Para estos efectos queda expresamente acordado que cualquier bonificaci&oacute;n y/o pago diferente al salario que pague <strong>EL EMPLEADOR </strong>en virtud del presente contrato y que reciba <strong>EL EMPLEADO </strong>no ser&aacute;n constitutivas de salario.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>NOVENO. Descuentos y Compensaciones.</strong> <strong>EL EMPLEADO</strong> autoriza desde ahora a <strong>EL EMPLEADOR</strong> para que de sus salarios, bonificaciones, prestaciones sociales e indemnizaciones (si hubiere lugar a ellas) le descuente, durante la vigencia del contrato o al momento de la terminaci&oacute;n del mismo por cualquier causa, las sumas de dinero que por cualquier motivo le llegare a adeudar.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>D&Eacute;CIMO. Confidencialidad. </strong><span lang=\"ES\">Adem&aacute;s de la obligaci&oacute;n propia e inherente que le corresponde a <strong>EL EMPLEADO</strong> de no divulgar informaci&oacute;n que por su naturaleza sea reservada o cuya divulgaci&oacute;n pueda ocasionar perjuicios a <strong>EL EMPLEADOR </strong>&nbsp;o a sus clientes, <strong>EL EMPLEADO</strong> declara y reconoce expresamente bajo este contrato que toda la informaci&oacute;n a la que acceder&aacute; y/o tendr&aacute; acceso, y/o le ser&aacute; divulgada y/o entregada en virtud y/o con ocasi&oacute;n de la relaci&oacute;n laboral es Informaci&oacute;n Confidencial&nbsp;y, por ende, es informaci&oacute;n privilegiada, sensible, de propiedad exclusiva de <strong>EL EMPLEADOR y/o sus clientes, </strong>y est&aacute; sujeta a reserva legal. En consecuencia, <strong>EL EMPLEADO</strong> se obliga a mantener como confidencial y sujeta a estricta reserva la informaci&oacute;n recibida, as&iacute; como a hacer uso adecuado de la misma.</span></span><span style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">El objeto de la cl&aacute;usula de confidencialidad es fijar los t&eacute;rminos y condiciones bajo los cuales, <strong>EL EMPLEADO </strong>mantendr&aacute; la confidencialidad de los datos e informaci&oacute;n que le sea entregada o puesta en conocimiento por <strong>EL EMPLEADOR y/o sus clientes,</strong> incluyendo informaci&oacute;n objeto de derecho de autor, patentes, t&eacute;cnicas, modelos, invenciones, know-how, procesos, algoritmos, programas, ejecutables, investigaciones, detalles de dise&ntilde;o, informaci&oacute;n financiera, lista de clientes, inversionistas, empleados, relaciones de negocios y contractuales, pron&oacute;sticos de negocios, planes de mercadeo o cualquier otra informaci&oacute;n constituyente de informaci&oacute;n confidencial y secretos empresariales relacionadas con &eacute;ste y/o terceros. Adicionalmente, mediante este contrato <strong>EL EMPLEADO</strong> se obliga a no revelar, divulgar, exhibir, mostrar, copiar, prestar, vender y/o comunicar la informaci&oacute;n que reciba de <strong>EL EMPLEADOR y/o sus clientes</strong> directamente a persona natural o jur&iacute;dica alguna, ni a utilizarla en favor propio o de terceros, y en consecuencia a mantenerla de manera confidencial y privada y a proteger dicha informaci&oacute;n para evitar su divulgaci&oacute;n no autorizada.</span><span style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">EL EMPLEADO </span></strong><span lang=\"ES\">expresamente manifiesta que cualquier informaci&oacute;n que le sea suministrada por <strong>EL EMPLEADOR y/o sus clientes</strong> directamente o a trav&eacute;s de cualquier medio en desarrollo de la ejecuci&oacute;n del contrato de trabajo, ser&aacute; mantenida en estricta confidencialidad y que s&oacute;lo podr&aacute; revelar informaci&oacute;n confidencial con autorizaci&oacute;n previa y escrita por parte de <strong>EL EMPLEADOR</strong> o cuando la divulgaci&oacute;n de dicha informaci&oacute;n sea necesaria para el desarrollo de su trabajo. Se considera tambi&eacute;n informaci&oacute;n confidencial:</span></span><span lang=\"ES\" style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">a.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">Aquella que como conjunto o por la configuraci&oacute;n o estructuraci&oacute;n exacta de sus componentes, no sea generalmente conocida entre los expertos en los campos correspondientes.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">b.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">La que no sea de f&aacute;cil acceso. </span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">c.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">Aquella informaci&oacute;n o documentaci&oacute;n que no est&eacute; sujeta a medidas de protecci&oacute;n razonables, de acuerdo con las circunstancias del caso, a fin de mantener su car&aacute;cter confidencial.&nbsp;</span></span><span lang=\"ES\" style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">Toda informaci&oacute;n entregada o puesta en conocimiento a <strong>EL EMPLEADO</strong> por <strong>EL EMPLEADOR y/o sus clientes</strong>, es de propiedad de <strong>EL EMPLEADOR y/o sus clientes</strong> y, en consecuencia, <strong>EL EMPLEADO</strong> no podr&aacute; utilizarla para fines distintos al desarrollo del contrato de trabajo.</span><span style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">Una vez finalizada la relaci&oacute;n laboral, <strong>EL EMPLEADO </strong>se obliga a realizar la entrega correspondiente de la informaci&oacute;n manejada de propiedad de <strong>EL EMPLEADOR y/o cualquiera de sus clientes, </strong>as&iacute; como cualquier copia de la misma,&nbsp;a quien <strong>EL EMPLEADOR</strong> designe para el efecto.</span><strong style=\"font-family:  sans-serif;\"><span lang=\"ES\">&nbsp;</span></strong></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">La presente obligaci&oacute;n de confidencialidad tendr&aacute; una duraci&oacute;n indefinida desde el momento de vinculaci&oacute;n de <strong>EL EMPLEADO.</strong> En el evento de extinguirse por cualquier raz&oacute;n la relaci&oacute;n laboral, <strong>EL EMPLEADO </strong>devolver&aacute; a <strong>EL EMPLEADOR</strong> la informaci&oacute;n confidencial trasmitida de manera escrita, grabada, en medio magn&eacute;tico o de cualquier otra forma tangible, o en su defecto proceder&aacute; a destruirla con previa autorizaci&oacute;n de este &uacute;ltimo y en presencia de uno de sus representantes.</span><span style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"ES\" style=\"font-family:  sans-serif;\">No habr&aacute; deber alguno de confidencialidad por parte de <strong>EL EMPLEADO</strong> en los siguientes casos:</span><span style=\"font-family:  sans-serif;\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">a.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">Cuando la informaci&oacute;n recibida sea de dominio p&uacute;blico.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">b.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">Cuando la informaci&oacute;n deje de ser confidencial por ser revelada por el propietario.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 18pt; text-indent: -18pt; text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">c.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-variant-position: normal; font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><span lang=\"ES\">Cuando deba ser revelada por&nbsp;<strong>EL EMPLEADO</strong> en cumplimiento de una disposici&oacute;n legal o reglamentaria, o en el marco de un proceso judicial, administrativo, arbitral o de naturaleza similar. </span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><span lang=\"ES\">La violaci&oacute;n de la confidencialidad o el uso indebido de la informaci&oacute;n por parte de <strong>EL EMPLEADO</strong> dar&aacute; derecho a <strong>EL EMPLEADOR</strong>, a dar por terminado el presente contrato de trabajo con justa causa, y a iniciar las acciones legales civiles y/o penales correspondientes en contra de <strong>EL EMPLEADO</strong>, tendientes a obtener el resarcimiento de sus perjuicios.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong>D&Eacute;CIMO PRIMERO. </strong><strong><span lang=\"es\">Herramientas de trabajo. </span></strong><span lang=\"es\">Corresponde a <strong>EL EMPLEADOR</strong> suministrar los elementos necesarios para el normal desempe&ntilde;o de las funciones del cargo contratado, por lo que dichas herramientas de trabajo deber&aacute;n ser custodiadas por <strong>EL EMPLEADO</strong> y &eacute;ste los devolver&aacute; a <strong>EL EMPLEADOR</strong> en el mismo estado en que le fueron entregadas, salvo el deterioro normal por el uso, a la terminaci&oacute;n del presente contrato. En este sentido, en caso de presentarse un da&ntilde;o que afecte el normal funcionamiento de lo entregado, por cualquier causa, <strong>EL EMPLEADO</strong> deber&aacute; informar por escrito a <strong>EL EMPLEADOR</strong> dentro de los tres (3) d&iacute;as calendario siguientes.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">D&Eacute;CIMO SEGUNDO. </span></strong><span lang=\"ES\">En todo aquello sobre lo cual no haya regulaci&oacute;n expresa en el presente documento, teniendo en cuenta el Reglamento Interno de Trabajo (si lo hubiere), ser&aacute; la ley colombiana laboral la que entre a regir la relaci&oacute;n entre las partes.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family:  sans-serif;\"><strong><span lang=\"ES\">D&Eacute;CIMO TERCERO. </span></strong><span lang=\"ES\">De conformidad con la Ley 1581 de 2012 relacionada con el Tratamiento de Datos Personales, se anexa al presente contrato ?AUTORIZACI&Oacute;N PARA TRATAMIENTO DE DATOS ? EMPLEADOS? debidamente suscrito por <strong>EL EMPLEADO.</strong></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\">&nbsp;<span style=\"font-family:  sans-serif;\">Para constancia y aceptaci&oacute;n de lo que en este documento se manifiesta se firma por las partes, el d&iacute;a (DIA) del (MES) de (ANO)&nbsp;en dos ejemplares iguales.</span></p>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n<table style=\"border-collapse: collapse; width: 95%; height: 220.664px; margin-left: auto; margin-right: auto;\">\r\n<tbody>\r\n<tr style=\"height: 22.3828px;\">\r\n<td style=\"width: 49.9761%; height: 22.3828px;\"><strong>EL EMPLEADOR,</strong>&nbsp;&nbsp;</td>\r\n<td style=\"width: 49.9761%; height: 22.3828px;\"><strong>EL TRABAJADOR</strong></td>\r\n</tr>\r\n<tr style=\"height: 22.3828px;\">\r\n<td style=\"width: 49.9761%; height: 22.3828px;\"><strong><span lang=\"ES\" style=\"font-size: 12.0pt; font-family: sans-serif;\">ESTRATEGIA DIGITAL Y COMERCIAL S.A.S</span></strong></td>\r\n<td style=\"width: 49.9761%; height: 22.3828px;\">(FIRMA_EMPLEADO)</td>\r\n</tr>\r\n<tr style=\"height: 22.3828px;\">\r\n<td style=\"width: 49.9761%; height: 22.3828px;\">NIT. 901658649-2</td>\r\n<td style=\"width: 49.9761%; height: 22.3828px;\">&nbsp;C.C.&nbsp; (CEDULA)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"MsoNormal\">&nbsp;</p>\r\n</div>\r\n<p class=\"MsoNormal\" style=\"text-align: center;\">&nbsp;</p>\r\n<p>&nbsp;</p>";
                    }
                    else
                    {
                        string msg = "No tiene saldo la plataforma";                            
                        responseModels.Message = msg;
                        responseModels.code = "200";
                        _ = utilities.SendEmail(dataUser.Rows[0]["EmailClient"].ToString(), "SALDO INSUFICIENTE COBRE", msg, false, "");
                    }
                }
            }
            catch (Exception)
            {
                responseModels.Message = "Error interno";
                responseModels.code = "500";
            }
            return responseModels;
        }
        public PaymentClass PutPaymentClass(DataTable dataUser)
        {            
            List<NoveltyDetail>? noveltyDetailList = new();
            NoveltyDetail? noveltyDetail = new();
            Beneficiary beneficiary = new();
            BankInfo bankInfo = new();
            PaymentClass paymentClass = new();

            var document = dataUser.Rows[0]["documentType"].ToString();
            if (!string.IsNullOrEmpty(document))
                beneficiary.documentType = document;
            else
                return null;

            var documentNumber = dataUser.Rows[0]["documentNumber"].ToString();
            if (!string.IsNullOrEmpty(documentNumber))
                beneficiary.documentNumber = documentNumber;

            else
                return null;

            var name = dataUser.Rows[0]["name"].ToString();
            if (!string.IsNullOrEmpty(name))
                beneficiary.name = name;
            else
                return null;

            var lastName = dataUser.Rows[0]["lastName"].ToString();
            if (!string.IsNullOrEmpty(lastName))
                beneficiary.lastName = lastName;

            else
                return null;

            var email = dataUser.Rows[0]["email"].ToString();
            if (!string.IsNullOrEmpty(email))
                beneficiary.email = email;
            else
                return null;

            var phone = dataUser.Rows[0]["phone"].ToString();
            if (!string.IsNullOrEmpty(phone))
                beneficiary.phone = phone;
            else
                return null;

            if (!string.IsNullOrEmpty(dataUser.Rows[0]["bankCode"].ToString()))
                bankInfo.bankCode = dataUser.Rows[0]["bankCode"].ToString();
            else
                return null;

            if (!string.IsNullOrEmpty(dataUser.Rows[0]["accountType"].ToString()))
                bankInfo.accountType = dataUser.Rows[0]["accountType"].ToString();
            else
                return null;

            if (!string.IsNullOrEmpty(dataUser.Rows[0]["accountNumber"].ToString()))
                bankInfo.accountNumber = dataUser.Rows[0]["accountNumber"].ToString();
            else
                return null;

            var totalAmount1 = int.Parse(dataUser.Rows[0]["totalAmount"].ToString());

            beneficiary.bankInfo = bankInfo;
            noveltyDetail.totalAmount = totalAmount1;
            noveltyDetail.reference = "Id_Anticipo - " + dataUser.Rows[0]["id_anticipo"].ToString();            
            noveltyDetail.beneficiary = beneficiary;
            noveltyDetailList.Add(noveltyDetail);
            paymentClass.controlRecord = 1;           
            paymentClass.noveltyDetails = noveltyDetailList;

            return paymentClass;
        }
        public string WeebHookPayment(TransactionRequest transactionRequest)
        {
            AdvanceCore advanceCore = new(_configuration);
            Utilities utilities = new(_configuration);
            AdvanceModel advanceModel = new(_configuration);
            AdvanceRequest advanceRequest = new();
            advanceRequest.uuid = transactionRequest.NoveltyUuid;
            // campo dinamica, se envia toda la respuesta del weebhook
            advanceRequest.AdvanceAmount = JsonConvert.SerializeObject(transactionRequest);
            string bodyEmail;

            try
            {
                if (transactionRequest.Status == "FINISHED")
                {
                    DataTable dataUser = advanceModel.PostAdvance(advanceRequest, 6);
                    if (dataUser.Rows[0]["state"].ToString() == "2")
                    {
                        return "205";
                    }
                   if (!advanceCore.CreateContract(dataUser)) advanceCore.CreateContract(dataUser);

                    bodyEmail = utilities.GetBodyEmailCode("", dataUser, 5);
                    var email =  utilities.SendEmail(dataUser.Rows[0]["email"].ToString(), "Anticipo consignado", bodyEmail, true,
                                    _configuration["route:pathContrato"] + $"\\{dataUser.Rows[0]["id_anticipo"]}.pdf");
                   return "201";
                }
                else
                {
                    advanceRequest.DescriptionsCobre = transactionRequest.DescriptionStatus;
                    DataTable dataUser = advanceModel.PostAdvance(advanceRequest, 7);
                    if (dataUser.Rows[0]["state"].ToString() == "2")
                    {
                        return "205";
                    }
                    //consultar el por que del rechazo
                    string code = "200";
                    if (dataUser.Rows[0]["state"].ToString() == "1")
                    {
                        bodyEmail = utilities.GetBodyEmailCode("", dataUser, 2);
                        var email = utilities.SendEmail(dataUser.Rows[0]["email"].ToString(), "Anticipo rechazado", bodyEmail, true, "");
                        code = "204";
                    }

                    return code;
                }
            }
            catch (Exception ex)
            {
                return "500 " + ex.Message;
            }
        }
    }
